	/**
	 * verifie si le format correspond a celui d'un mot de passe
	 * @param password mot de passe
	 * @return true si c'est verifie sinon false
	 */
    public boolean checkPassword(String password) {
        String passwordRegex = "^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%^&-+=()])(?=\\S+$).{5,20}$";
        if (password == null)
            return false;
        return password.matches(passwordRegex);
    }
	
	/**
	 * verifie si le format correspond a celui d'un prenom
	 * @param name le prenom
	 * @return true si c'est verifie sinon false
	 */
    public boolean checkName(String name) {
        String nameRegex = "^([a-zA-Z]|[à-ú]|[À-Ú])+$";
        if (name == null)
            return false;
        if (name.length() < 1 || name.length() > 20)
            return false;
        return name.matches(nameRegex);
    }
	
	/**
	 * verifie si le format correspond a celui d'un nom
	 * @param surname le nom
	 * @return true si c'est verifie sinon false
	 */
    public boolean checkSurname(String surname) {
        String surnameRegex = "^([a-zA-Z]|[à-ú]|[À-Ú])+$";
        if (surname == null)
            return false;
        if (surname.length() < 1 || surname.length() > 20)
            return false;
        return surname.matches(surnameRegex);
    }
	
	/**
	 * verifie si le format correspond a celui d'une localisation
	 * @param location la localisation
	 * @return true si c'est verifie sinon false
	 */
    public boolean checkLocation(String location) {
        String locationRegex = "^([a-zA-Z]|[à-ú]|[À-Ú])+$";
        if (location == null)
            return false;
        if (location.length() < 1 || location.length() > 20)
            return false;
        return location.matches(locationRegex);
    }
	
	/**
	 * verifie si le format correspond a celui d'un telephone
	 * @param phone le telephone
	 * @return true si c'est verifie sinon false
	 */
    public boolean checkPhone(String phone) {
        String phoneRegex = "^\\d{10}$";
        if (phone == null)
            return false;
        return phone.matches(phoneRegex);
    }

